insert into gnc_obstax.t_obstax (uuid_sinp, id_program, cd_nom, specie, date, id_role, count, timestamp_create, geom)

  with g as (select
               row_number()
               over (
                 order by random ) as id
               , id_program
               , geom
             from (
                    select
                      id_program
                      , RandomPointsInPolygon((ST_Dump(ST_SetSRID((st_makevalid(geom)), 4326))).geom,
                                              50) as geom
                      , random()                   as random
                    from gnc_core.t_programs) as t

  )
    , d as (
      select
        row_number()
        over () as id
        , t.*
      from (
             select

               bib_noms.cd_nom
               , bib_noms.nom_francais                                     specie
               , (timestamp '2018-06-01' +
                  random() * (timestamp '2018-12-11 20:00:00' -
                              timestamp '2018-06-01 10:00:00')) :: date as date
               , t_users.id_user
               , floor(random() * 10 + 1) :: int                        as count
               , now()                                                  as now
               --         , (
               --             select RandomPointsInPolygon((ST_Dump(ST_SetSRID((
               --                                                                select st_union(st_makevalid(geom)) geom
               --                                                                from gnc_core.t_programs), 4326))).geom, 1)
               --             limit 1)                                             as geom
               , random()                                               as random
             from generate_series(1, 500) cross join taxonomie.bib_noms
               cross join gnc_core.t_users
             order by random()
             limit 500) as t
  ) select
      uuid_generate_v4()
        , g.id_program
        , d.cd_nom
        , d.specie
      , d.date
      , d.id_user
      , d.count
      , d.now
      , g.geom
    from d, g
    where d.id = g.id
;
